mapper.select:
  	SELECT 
  		COUNT(*)
  	FROM sal_reviewer r

		 INNER JOIN act_ru_task t ON t.PROC_INST_ID_ = r.process_instance_id AND t.ASSIGNEE_ = #{assignee}



			 AND r.quotation_no LIKE '%${quotationNo}%-%'


			 AND r.quotation_no LIKE '%-${quotationYear}%'


			 AND r.project_leader LIKE '%${projectLeader}%'


			 AND r.create_time >= #{startCreateTime}


			 AND r.create_time <= #{endCreateTime}


			 AND r.is_cost = #{isCost}

SELECT
r.id id, r.quotation_no quotationNo, r.create_time createTime, r.project_leader projectLeader, r.sal_preparer salPreparer
FROM sal_reviewer r, act_hi_procinst p
WHERE p.PROC_INST_ID_ = r.process_instance_id AND p.END_TIME_ IS NOT NULL
AND quotation_no LIKE '%${quotationNo}%-%'
AND quotation_no LIKE '%-%${quotationYear}%'
AND create_time >= #{startCreateTime}
AND create_time <= #{endCreateTime}
AND is_cost = #{isCost}
ORDER BY RIGHT(quotationNo,2) ${order},LEFT(quotationNo,6) ${order}
ORDER BY #{sort} ${order}
ORDER BY RIGHT(quotationNo,2) DESC,LEFT(quotationNo,6) DESC
LIMIT #{startPage},#{rows}
