mapper.select:
SELECT COUNT(*) FROM sal_work_card c
INNER JOIN act_ru_task t ON t.PROC_INST_ID_ = c.process_instance_id AND t.ASSIGNEE_ = #{assignee}
AND c.quotation_no LIKE '%${quotationNo}%-%'
AND c.quotation_no LIKE '%-${quotationYear}%'
AND c.work_card_no LIKE '%${quotationNo}%-%'
AND c.work_card_no LIKE '%-${quotationYear}%'
AND c.work_card_leader LIKE '%${workCardLeader}%'
AND c.work_card_date >= #{startWorkCardDate}
AND c.work_card_date <= #{endWorkCardDate}
